INSERT INTO `wp_posts` VALUES("466", "1", "2015-08-13 23:01:45", "2015-08-13 23:01:45", "[vc_row][vc_column width=\"1/1\"][vc_column_text pl=\"0\" pr=\"0\" pt=\"0\" pb=\"0\"]\n<h2 style=\"text-align: center; color: #243285;\"><strong>Imagenología/Radiología</strong></h2>\n[/vc_column_text][nz_sep type=\"solid\" color=\"#ec1c24\" top=\"10\" bottom=\"20\" width=\"110\" height=\"1\" align=\"center\"][vc_column_text pl=\"15\" pr=\"15\" pt=\"15\" pb=\"15\" back_color=\"#e7e7e8\"]El área de imágenes contribuye al diagnóstico por parte de los médicos. Las imágenes de calidad como las proporcionadas por nuestros equipos, contribuyen el diagnóstico médico. El avance de la tecnología es nuestra prioridad al memento de realizar exámenes.\n\nContamos con ecografía 3D y 4D, radiografía, electro de esfuerzo, y un tomógrafo de última generación SIEMENS SCOPE.\n\nEl departamento cuenta con médicos radiólogos, tecnólogos, técnicos y operador de equipos, personal indispensable para una atención de calidad y el correcto funcionamiento de los equipos.[/vc_column_text][vc_gallery images=\"542,544,543\" link_full=\"false\" version=\"grid\" autoplay=\"false\" img_size=\"Ninzio-One-Third\" img_size_carousel=\"medium\" columns=\"3\" columns_carousel=\"1\" animate=\"none\"][vc_gallery images=\"546,547\" link_full=\"false\" version=\"grid\" autoplay=\"false\" img_size=\"Ninzio-One-Third\" img_size_carousel=\"medium\" columns=\"2\" columns_carousel=\"1\" animate=\"none\"][/vc_column][/vc_row]", "Imagenología/Radiología", "", "inherit", "open", "open", "", "60-autosave-v1", "", "", "2015-08-13 23:01:45", "2015-08-13 23:01:45", "", "60", "http://hospitalduran.com/index.php/2015/08/11/60-autosave-v1/", "0", "revision", "", "0");
